-- ----------------------------
-- Procedure structure for delete_idx
-- ----------------------------
DROP PROCEDURE IF EXISTS `delete_idx`;
DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `delete_idx`(IN p_tablename varchar(200), IN p_idxname VARCHAR(200))
begin  
DECLARE str VARCHAR(250);  
  set @str=concat(' drop index ',p_idxname,' on ',p_tablename);   
    
  select count(*) into @cnt from information_schema.statistics where table_name=p_tablename and index_name=p_idxname ;  
  if @cnt >0 then   
    PREPARE stmt FROM @str;  
    EXECUTE stmt ;  
  end if;  
end
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for p_create_index
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_create_index`;
DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `p_create_index`()
begin
   declare tableName varchar(50);
	 declare columnName varchar(100);
   declare tableIndex varchar(100);
   declare vsql varchar(1000);
     /**跳出循环标识**/
   declare done1 int default false;
   declare done2 int default false;
     /**声明游标**/
   declare cur1 cursor for
	 select t.table_name from information_schema.`TABLES` t where t.TABLE_SCHEMA='jn_spark_new' and t.TABLE_NAME not like 'SYS\_%';
   /**循环结束设置跳出标识**/
   declare continue handler for not found set done1 = true;
   /**打开游标**/
  set done1=false;
   open cur1;
	 loop_lable1:loop
        fetch cur1 into tableName;
				if done1 then
					leave loop_lable1;
				end if;
				call delete_idx(tableName,concat(tableName,'_idx'));
        set tableIndex='';
				begin
				declare cur2 cursor for
				select t.column_name from information_schema.`COLUMNS` t where t.TABLE_SCHEMA='jn_spark_new' and t.TABLE_NAME =tableName and t.COLUMN_NAME like '%\_id';
				/**循环结束设置跳出标识**/
				declare continue handler for not found set done2 = true;
				set done2=false;
				/**打开游标**/
				open cur2;
				loop_lable2:loop
					fetch cur2 into columnName;
					if done2 then
						leave loop_lable2;
					end if;
					if tableIndex='' then
						set tableIndex=columnName;
					else
						set tableIndex=concat(tableIndex,',',columnName);
					end if;
					insert lsb_tmp(tid,mid) values(tableName,columnName);
				end loop;
				/**关闭游标**/
				close cur2;
				if length(tableIndex)>0  then
          set vsql =concat('create index ',concat(tableName,'_idx'),' on ',tableName,'(',tableIndex,')');
          insert into lsb_index(tname,tindex,vsql) values(tableName,tableIndex,vsql);
          set @vsql=vsql;
          prepare stmt from @vsql;
					execute stmt;
					deallocate prepare stmt;
        end if;
        end;
   end loop;
     /**关闭游标**/
   close cur1;
end
;;
DELIMITER ;

