DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `p_deal_paper_structure`()
begin
   declare pid varchar(50);
	 declare name varchar(100);
   declare type varchar(100);
   declare code varchar(5);
   declare count int;
   declare tid,tid1 varchar(32);
   declare structure_id varchar(32);
   declare h_name varchar(32);
   declare subject_type varchar(32);
	 declare h_content varchar(1024);
   
     /**跳出循环标识**/
   declare done int default false;
   declare done2 int default false;
	 declare done3 int default false;
     /**声明游标**/
   declare cur cursor for
	 select a.id,a.name,b.type,b.section_code from tb_paper a join tb_paper_catalog b on a.catalog_id=b.id;
   /**循环结束设置跳出标识**/
   declare continue handler for not found set done = true;
   /**打开游标**/
   open cur;
	 loop_lable:loop
        fetch cur into pid,name,type,code;
        if done then
					leave loop_lable;
				end if;
				if(type=1) then
					truncate table lsb_paper_structure;
          -- 处理听力1级
          select f_get_uuid() into tid from dual;
					insert into tb_paper_structure(`id`,`content_type`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
					select tid,1,pid,'Part I','听力','0','',1,0,now(),now(),1,0 from dual;
          -- 处理听力2级
          begin
					declare existed int;
          declare cur2 cursor for
					select a.id,a.h_name,a.subject_type,a.h_content from spark_english_online.matched_papers_topic_hearing a join spark_english_online.matched_papers b on a.mp_id=b.id join spark_english_online.matched_papers_type c on b.mpt_id=c.id where b.is_del=0 and a.is_del=0 and c.id not in ('402880ee552d89fa01552dc930b30001','402880ee552d89fa01552dc9620f0002') and a.mp_id=pid;
					declare continue handler for not found set done2 = true;
					set done2=false;
					open cur2;
					loop_lable2:loop
					fetch cur2 into structure_id,h_name,subject_type,h_content;
					if done2 then
						leave loop_lable2;
					end if;
          set @existed=0;
					select  @existed :=t.new_id from lsb_paper_structure t where t.paper_id=pid and t.h_name=h_name and t.subject_type=subject_type limit 0,1;
					if @existed!=0 and @existed is not null then
							insert into lsb_paper_structure(`current_id`, `parent_id`, `paper_id`, `h_name`, `subject_type`)
							select structure_id,@existed,pid,h_name,subject_type from dual; 
          else 
							select f_get_uuid() into tid1 from dual;
							insert into lsb_paper_structure(new_id,`current_id`, `parent_id`, `paper_id`, `h_name`, `subject_type`)
							select tid1,structure_id,tid1,pid,h_name,subject_type from dual; 
							insert into tb_paper_structure(`id`,`content_type`,`content`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
							select  tid1,1,h_content,pid,h_name,(select m.name from spark_english_online.sys_config m where m.type='MTPH_TYPE' and m.value=subject_type),tid,0,2,0,now(),now(),0,0 from dual;
          end if;
					end loop;
					close cur2;
					-- 处理听力三级
					insert into tb_paper_structure(`id`,`content_type`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
					select a.id,1,pid,a.`name`,(select m.name from spark_english_online.sys_config m where m.type='MTPH_TYPE' and m.value=b.subject_type),b.parent_id,0,3,1,now(),now(),0,0 from spark_english_online.matched_papers_topic_hearing_type a join lsb_paper_structure b on a.mpth_id=b.current_id and b.paper_id=pid;
          end;
					truncate table lsb_paper_structure;
					-- 处理阅读一级
					select f_get_uuid() into tid from dual;
					insert into tb_paper_structure(`id`,`content_type`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
          select tid,2,pid,'Part II','阅读','0','',1,0,now(),now(),2,0 from dual;
					-- 处理阅读二级
					begin
					declare existed int;
          declare cur3 cursor for
					select a.id,a.r_name,a.subject_type,a.r_content from spark_english_online.matched_papers_topic_read a join spark_english_online.matched_papers b on a.mp_id=b.id join spark_english_online.matched_papers_type c on b.mpt_id=c.id where b.is_del=0 and a.is_del=0 and c.id not in ('402880ee552d89fa01552dc930b30001','402880ee552d89fa01552dc9620f0002') and a.mp_id=pid;
					declare continue handler for not found set done3 = true;
					set done3=false;
					open cur3;
					loop_lable3:loop
					fetch cur3 into structure_id,h_name,subject_type,h_content;
					if done3 then
						leave loop_lable3;
					end if;
          set @existed=0;
					select  @existed :=t.new_id from lsb_paper_structure t where t.paper_id=pid and t.h_name=h_name and t.subject_type=subject_type limit 0,1;
					if @existed!=0 and @existed is not null then
							insert into lsb_paper_structure(`current_id`, `parent_id`, `paper_id`, `h_name`, `subject_type`)
							select structure_id,@existed,pid,h_name,subject_type from dual; 
          else 
							select f_get_uuid() into tid1 from dual;
							insert into lsb_paper_structure(new_id,`current_id`, `parent_id`, `paper_id`, `h_name`, `subject_type`)
							select tid1,structure_id,tid1,pid,h_name,subject_type from dual; 
							insert into tb_paper_structure(`id`,`content_type`,`content`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
							select  tid1,2,h_content,pid,h_name,(select m.name from spark_english_online.sys_config m where m.type='MTPR_TYPE' and m.value=subject_type),tid,0,2,0,now(),now(),0,0 from dual;
          end if;
					end loop;
					close cur3;
					-- 处理阅读三级
					insert into tb_paper_structure(`id`,`content_type`,`paper_id`,`name`,`alias`,`parent_id`,`parent_ids`,`level`,`is_leaf`,`create_date`,`update_date`,`sort`,`del_flag`)
					select a.id,2,pid,a.type_name,(select m.name from spark_english_online.sys_config m where m.type='MTPR_TYPE' and m.value=b.subject_type),b.parent_id,0,3,1,now(),now(),0,0 from spark_english_online.matched_papers_topic_read_type a join lsb_paper_structure b on a.mptr_id=b.current_id and b.paper_id=pid;
