-- ----------------------------
-- Procedure structure for p_deal_qrcode
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_deal_qrcode`;
DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `p_deal_qrcode`()
BEGIN
  truncate table lsb_qr_code;
	insert into lsb_qr_code(mp_id,qr_code,section_code)
	select a.id,substring_index(substring_index(a.qr_code,',',b.help_topic_id+1),',',-1),replace(c.sc_id,'0','')
	from spark_english_online.matched_papers a join spark_english_online.matched_papers_type c on a.mpt_id=c.id join lsb_help_topic b
	on b.help_topic_id < (length(a.qr_code) - length(replace(a.qr_code,',',''))+1)
	where a.mpt_id in('402880ee552d89fa01552dc930b30001','402880ee552d89fa01552dc9620f0002') and a.is_del=0
	order by a.id;
	update lsb_qr_code a join (
  select qr_code,count(1) as num from lsb_qr_code where length(qr_code)>1 group by qr_code) b
  on a.qr_code=b.qr_code set a.count=b.num;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for p_deal_qrcode_section
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_deal_qrcode_section`;
DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `p_deal_qrcode_section`()
BEGIN
  truncate table lsb_qr_section;
	insert into lsb_qr_section(qr_code,section_code)
	select qr_code, if(locate('CET4',t.type_name),1,2) from spark_english_online. j_common_type t ;
	insert into lsb_qr_section(qr_code,section_code)
	select qr_code, if(locate('CET4',t.name),1,2) from spark_english_online. j_qr_fragmentation t;
	insert into lsb_qr_section(qr_code,section_code)
	select a.qr_code,replace(b.level_type,'0','') from spark_english_online.j_qr_nwt a join spark_english_online.j_qr_nw b on a.id=b.qrnwt_id;
	insert into lsb_qr_section(qr_code,section_code)
	select t.qr_code,replace(t.level_type,'0','') from spark_english_online.j_qr_subtitle_listening t;
	insert into lsb_qr_section(qr_code,section_code)
	select a.qr_code,replace(b.level_type,'0','') from (
	select a.id,substring_index(substring_index(a.qr_code,',',b.help_topic_id+1),',',-1) as qr_code
	from 
	spark_english_online.j_qr_writing_type a join mysql.help_topic b	on b.help_topic_id < (length(a.qr_code) - length(replace(a.qr_code,',',''))+1)
  order by a.id) as a
	join spark_english_online.j_qr_writing b on a.id=b.jqrwt_id;
	insert into lsb_qr_section(qr_code,section_code)
	select distinct qr_code, if(locate('CET4',t.title),1,2) from spark_english_online.j_qr_study_materials_writing t;
END
;;
DELIMITER ;

