DELIMITER ;;
CREATE DEFINER=`sparkdb`@`%` PROCEDURE `p_deal_teacher_id`(in str3 varchar(2))
begin
  declare i int(10);
  declare j int(10);
  declare k int(10);
  declare str1 varchar(100);
  declare str4 varchar(1000);
  declare str5 varchar(100);
  declare str6 varchar(100);
  declare str7 varchar(1000) default 'a';
 
  select count(*) into i from spark_english_online.network_course;
	truncate table lsb_teacher;
	select i;
  set j=0;
  while j<i do
		select id,teacher_id into str1,str4 from spark_english_online.network_course limit j,1;
    
		select instr(str4,str3) into k from dual;
		if k=0 then
			select ifnull(t.`name`,'') into str6 from spark_english_online.network_course_teacher t where t.id=str4;

			insert into lsb_teacher(courseid,teacherid,teachername) values(str1,str4,str6);
		end if;
		while k!=0 do
			select substring_index(str4,str3,1) into str5 from dual;
      select ifnull(t.`name`,'') into str6 from spark_english_online.network_course_teacher t where t.id=str4;
			insert into lsb_teacher(courseid,teacherid,teachername) values(str1,str5,str6);
			select mid(str4,k+1) into str4 from dual;
			select instr(str4,str3) into k from dual;
		end while;
  set j=j+1;
  end while;
 end
;;
DELIMITER ;

